async function pageFunction(context) {
  const { request, jQuery, log, Apify } = context;
  const includeTerms = ['strip', 'puck', 'neon', 'rope', 'accent', 'glow'];
  const excludeTerms = ['table lamp', 'floor lamp', 'desk lamp', 'lantern', 'sconce'];
  const items = [];
  const cards = jQuery('[data-testid=\'item\']');
  cards.each((_, el) => {
    const $el = jQuery(el);
    const titleEl = $el.find('[data-testid=\'product-title\'] span').first();
    const name = (titleEl.text() || $el.find('a span').first().text() || '').trim();
    if (!name) return;
    const lowered = name.toLowerCase();
    if (!includeTerms.some(term => lowered.includes(term))) return;
    if (excludeTerms.some(term => lowered.includes(term))) return;
    const link = $el.find('a').attr('href');
    const price = $el.find('[data-automation-id=\'product-price\'] span').first().text().trim() || null;
    const badge = $el.find('[data-testid=\'sponsored\']').text().trim() || null;
    items.push({
      retailer: 'Walmart',
      name,
      url: link ? new URL(link, 'https://www.walmart.com').href : null,
      price,
      badge,
      source: request.url
    });
  });
  log.info(`Extracted ${items.length} accent SKUs`);
  await Apify.pushData(items);
}
